import React, { useState } from 'react';

const ImageSlider = () => {
  // Use the useState hook to track the current index of the image that is being shown
  const [currentIndex, setCurrentIndex] = useState(0);

  // An array of images that will be shown in the slider
  const images = [
    'image1.jpg',
    'image2.jpg',
    'image3.jpg',
    'image4.jpg'
  ];

  // A function that will be called to move to the previous image in the array
  const prevImage = () => {
    // If the current index is the first image in the array, move to the last image, otherwise move to the previous image
    setCurrentIndex(currentIndex === 0 ? images.length - 1 : currentIndex - 1);
  }

  // A function that will be called to move to the next image in the array
  const nextImage = () => {
    // If the current index is the last image in the array, move to the first image, otherwise move to the next image
    setCurrentIndex(currentIndex === images.length - 1 ? 0 : currentIndex + 1);
  }

  return (
    <div>
      {/* The img element that will show the current image in the slider */}
      <img src={images[currentIndex]} alt="Slider image" />

      {/* Buttons to move to the previous and next images */}
      <button onClick={prevImage}>Previous</button>
      <button onClick={nextImage}>Next</button>
    </div>
  );
}
