import React, { useState } from 'react';

function ImageSlider() {
  // Use the useState hook to store the current image index
  const [currentIndex, setCurrentIndex] = useState(0);

  // Define a list of images
  const images = [
    'https://picsum.photos/id/1/200/300',
    'https://picsum.photos/id/2/200/300',
    'https://picsum.photos/id/3/200/300'
  ];

  // Define a function to go to the next image
  const nextImage = () => {
    // If we're at the last image, go back to the first image.
    // Otherwise, go to the next image.
    const nextIndex = currentIndex === images.length - 1 ? 0 : currentIndex + 1;
    setCurrentIndex(nextIndex);
  }

  // Define a function to go to the previous image
  const prevImage = () => {
    // If we're at the first image, go to the last image.
    // Otherwise, go to the previous image.
    const prevIndex = currentIndex === 0 ? images.length - 1 : currentIndex - 1;
    setCurrentIndex(prevIndex);
  }

  return (
    <div>
      {/* Display the current image */}
      <img src={images[currentIndex]} alt="Image" />

      {/* Add buttons to go to the next and previous images */}
      <button onClick={prevImage}>Previous</button>
      <button onClick={nextImage}>Next</button>
    </div>
  );
}
