import React from 'react';
import Slider from 'react-slick';

import LoginForm from "../components/LoginForm";

const Home = (props) => {

  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    arrows: false
  };

    return (
        <>
            <h1 class="index">Home</h1>

            {/* Ternary Operator */}
            {(!props.authenticated) ? (
                // Passing onAuthenticated to Login Form 
                < LoginForm onAuthenticated={props.onAuthenticated} />
            ) : (
                <p class="index">You are logged in</p>
                
            )}

<Slider {...settings}>
      <div>
        <img src="https://placekitten.com/800/400" alt="cat" />
      </div>
      <div>
        <img src="https://placekitten.com/800/401" alt="cat" />
      </div>
      <div>
        <img src="https://placekitten.com/800/402" alt="cat" />
      </div>
    </Slider>
        </>
    );
};

export default Home;
